import { Add, Remove } from "@material-ui/icons";
import {useEffect, useState} from 'react';
import styled from "styled-components";
import Announcement from "../components/Announcement";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import Newsletter from "../components/Newsletter";
import { mobile } from "../responsive";
import {useLocation} from 'react-router-dom';
import {publicRequest,userRequest} from './../requestMethod';
import { useDispatch } from "react-redux";
import { addProduct } from "../redux/cartRedux";
const Container = styled.div``;

const Wrapper = styled.div`
  padding: 50px;
  display: flex;
  ${mobile({ padding: "10px", flexDirection:"column" })}
`;

const ImgContainer = styled.div`
  flex: 1;
`;

const Image = styled.img`
  width: 100%;
  height: 90vh;
  object-fit: cover;
  ${mobile({ height: "40vh" })}
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 0px 50px;
  ${mobile({ padding: "10px" })}
`;

const Title = styled.h1`
 
`;

const Desc = styled.p`
  margin: 20px 0px;
  font-size:19px;
`;

const Price = styled.span`
  font-weight: 100;
  font-size: 40px;
`;

const FilterContainer = styled.div`
  width: 50%;
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  ${mobile({ width: "100%" })}
`;

const Filter = styled.div`
  display: flex;
  align-items: center;
`;

const FilterTitle = styled.span`
  font-size: 20px;
  font-weight: 200;
`;

const FilterColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
  margin: 0px 5px;
  cursor: pointer;
`;

const FilterSize = styled.select`
  margin-left: 10px;
  padding: 5px;
  width:100px;
`;

const FilterSizeOption = styled.option``;

const AddContainer = styled.div`
  width: 50%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  ${mobile({ width: "100%" })}
`;

const AmountContainer = styled.div`
  display: flex;
  align-items: center;
  font-weight: 700;
`;

const Amount = styled.span`
  width: 30px;
  height: 30px;
  border-radius: 10px;
  border: 1px solid teal;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0px 5px;
`;

const Button = styled.button`
  padding: 15px;
  border: 2px solid teal;
  background-color: white;
  cursor: pointer;
  font-weight: 500;

  &:hover{
      background-color: #f8f4f4;
  }
`;

const Product = () => {
  const location=useLocation();
  const id=location.pathname.split("/")[2];
  const [product,setProduct]=useState({});
  const [quantity,setQuantity]=useState(1);
  const [color,setColor]= useState("");
  const [size,setSize]= useState("");
  const dispatch=useDispatch();
  useEffect(()=>{
    const getProduct=async ()=>{
      try{
         const res=await publicRequest.get("/products/find/"+id);
         setProduct(res.data);
         console.log(res.data)
      }
      catch{

      }};
      getProduct();
  },[id])
  const handleQuantity=(type)=>{
    if(type=="desc"&&quantity>1){
      setQuantity(quantity-1);
    }else if(type=="inc"){
      setQuantity(quantity+1);
    }
  }
  
  const handleClick=()=>{
    if(!color||!size) alert("Vui lòng chọn size và màu!")
    else{
      alert("Thêm sp thành công!")
      dispatch(
        addProduct({...product,quantity,color,size})
        );
    }
    
  };
  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
        <ImgContainer>
          <Image src={product.img} />
        </ImgContainer>
        <InfoContainer>
          <Title>{product.title}</Title>
          <Desc>
            {product.desc}
          </Desc>
          <Price>{product.price} đ</Price>
          <FilterContainer>
            <Filter>
            <FilterTitle>Màu sắc</FilterTitle>
            {product.color?.map((c) => (
                <FilterColor color={c} key={c} onClick={()=>{setColor(c)}} />
              ))}
              
            </Filter>
            <Filter>
              <FilterTitle>Size</FilterTitle>
              <FilterSize onChange={(e)=>{setSize(e.target.value)}}>
              <FilterSizeOption >Chọn size</FilterSizeOption>
              {product.size?.map((s) => (
                  <FilterSizeOption key={s}>{s}</FilterSizeOption>
                ))}
                   
              </FilterSize>
            </Filter>
          </FilterContainer>
          <AddContainer>
            <AmountContainer>
              <Remove onClick={()=>handleQuantity("desc")} />
              <Amount>{quantity}</Amount>
              <Add onClick={()=>handleQuantity("inc")}/>
            </AmountContainer>
            <Button onClick={handleClick}>THÊM VÀO GIỎ HÀNG</Button>
          </AddContainer>
        </InfoContainer>
      </Wrapper>
      <Newsletter />
      <Footer />
    </Container>
  );
};

export default Product;
