import { Add, Remove } from "@material-ui/icons";
import styled from "styled-components";
import Announcement from "../components/Announcement";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import { mobile } from "../responsive";
import { useSelector,useDispatch} from "react-redux";
import { useHistory } from "react-router";
import { useState,useEffect } from "react";
import { deleteCart } from "../redux/cartRedux";
const Container = styled.div``;

const Wrapper = styled.div`
  padding: 20px;
  ${mobile({ padding: "10px" })}
`;

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`;

const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  border: ${(props) => props.type === "filled" && "none"};
  background-color: ${(props) =>
    props.type === "filled" ? "black" : "transparent"};
  color: ${(props) => props.type === "filled" && "white"};
`;
const SelectVoucher=styled.select`

  padding: 5px;`;
const OptionVoucher=styled.option``;
const TopTexts = styled.div`
  ${mobile({ display: "none" })}
`;
const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
`;

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
  ${mobile({ flexDirection: "column" })}

`;

const Info = styled.div`
  flex: 3;
`;

const Product = styled.div`
  display: flex;
  justify-content: space-between;
  ${mobile({ flexDirection: "column" })}
`;

const ProductDetail = styled.div`
  flex: 2;
  display: flex;
`;

const Image = styled.img`
  width: 200px;
`;

const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;

const ProductName = styled.span``;

const ProductId = styled.span``;

const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
`;

const ProductSize = styled.span``;

const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const ProductAmount = styled.div`
  font-size: 24px;
  margin: 5px;
  ${mobile({ margin: "5px 15px" })}
`;

const ProductPrice = styled.div`
  font-size: 30px;
  font-weight: 200;
  ${mobile({ marginBottom: "20px" })}
`;

const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
`;

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 75vh;
`;

const SummaryTitle = styled.h1`
  font-weight: 200;
`;

const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === "total" && "500"};
  font-size: ${(props) => props.type === "total" && "24px"};
`;
const Address=styled.input`
placeholder:'Địa chỉ';
margin-left:20px;
`
;
const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: black;
  color: white;
  font-weight: 600;
`;

const Cart = () => {
  const dispatch=useDispatch();
  const cart = useSelector((state) =>state.cart);
  const voucher=useSelector((state) =>state.voucher);
  const [address,setAddress]=useState("");
  const [value,setValue]=useState(0);
  const history = useHistory();
  const currentUser = useSelector((state) => state.user.currentUser);
  console.log(currentUser)
  const point = useSelector((state) => state.user.point);
  let level=point/1000000;
  if(level<1) level=0;
  if(1<=level&&level<2) level=1;
  if(level<3&&level>=2) level=2
  if(level>=3) level=3;
  const onSubmited=() => {
    const makeRequest = async () => {
      try {
        history.push("/success", {
          stripeData: address,
          products: cart,
        value:value });
      } catch {}
    };
     makeRequest();
     
  }
  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
        <Title>GIỎ HÀNG </Title>
        <Top>
          <TopButton>TIẾP TỤC MUA SẮM</TopButton>
          
          
        </Top>
        <Bottom>
          <Info>
          {cart.products.map((product) => (
              <Product>
                <ProductDetail>
                  <Image src={product.img} />
                  <Details>
                    <ProductName>
                      <b>Product:</b> {product.title}
                    </ProductName>
                    <ProductId>
                      <b>ID:</b> {product._id}
                    </ProductId>
                    <ProductColor color={product.color} />
                    <ProductSize>
                      <b>Size:</b> {product.size}
                    </ProductSize>
                  </Details>
                </ProductDetail>
                <PriceDetail>
                  <ProductAmountContainer>
                    <Add />
                    <ProductAmount>{product.quantity}</ProductAmount>
                    <Remove />
                  </ProductAmountContainer>
                  <ProductPrice>
                     {product.price * product.quantity} đ
                  </ProductPrice>
                </PriceDetail>
              </Product>
            ))}
            <Hr></Hr>
          </Info>
          <Summary>
            <SummaryTitle>Đơn đặt hàng</SummaryTitle>
            <SummaryItem>
              <SummaryItemText>Số tiền</SummaryItemText>
              <SummaryItemPrice>{cart.total*(1-value)} đ</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem>
              <SummaryItemText>Phí ship</SummaryItemText>
              <SummaryItemPrice>50000 đ</SummaryItemPrice>
            </SummaryItem>
          
            <SummaryItem >
              <SummaryItemText>Tổng tiền( chưa giảm)</SummaryItemText>
              <SummaryItemPrice>{cart.total*(1-value)+50000} đ</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem >
              <SummaryItemText>Số tiền được giảm với cấp độ khách hàng thân thiện {level}</SummaryItemText>
              <SummaryItemPrice>{level*10000} đ</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem >
              <SummaryItemText>Dùng mã voucher
              <SelectVoucher onChange={(e)=>{ setValue(e.target.value/100)}}>
              <OptionVoucher value={0} >Chọn Voucher</OptionVoucher>
              {voucher.vouchers?.map((voucher)=>(
                <OptionVoucher key={voucher} value={voucher.value}>{voucher.desc}</OptionVoucher>
              ))}
              </SelectVoucher>
              </SummaryItemText>
              
            </SummaryItem>
            <SummaryItem type="total">
              <SummaryItemText>Tổng tiền</SummaryItemText>
              <SummaryItemPrice>{cart.total*(1-value)+50000-level*10000} đ</SummaryItemPrice>
            </SummaryItem>
            <SummaryItemText>Địa chỉ</SummaryItemText><Address onChange={(e)=>{setAddress(e.target.value)}}/>
            <Button onClick={onSubmited}>Đặt hàng</Button>
          </Summary>
        </Bottom>
      </Wrapper>
      <Footer />
    </Container>
  );
};

export default Cart;
